// Slug aliases for common variations
const slugAliases: Record<string, string> = {
  'js': 'javascript',
  'ts': 'typescript',
  'node': 'nodejs',
  'node-js': 'nodejs',
  'react-js': 'react',
  'reactjs': 'react',
  'vue': 'react', // Use React-like atom icon for Vue
  'vuejs': 'react',
  'angular': 'react',
  'frontend': 'html',
  'backend': 'nodejs',
  'fullstack': 'javascript',
  'full-stack': 'javascript',
  'web-development': 'html',
  'webdev': 'html',
  'datascience': 'data-science',
  'data_science': 'data-science',
  'machinelearning': 'machine-learning',
  'machine_learning': 'machine-learning',
  'deep-learning': 'machine-learning',
  'deeplearning': 'machine-learning',
  'ai-ml': 'ml',
  'artificial-intelligence': 'ai',
  'cyber-security': 'cybersecurity',
  'ethical-hacking': 'cybersecurity',
  'hacking': 'cybersecurity',
  'cloud-computing': 'cloud',
  'cloudcomputing': 'cloud',
  'amazon-web-services': 'aws',
  'microsoft-azure': 'azure',
  'google-cloud': 'gcp',
  'google-cloud-platform': 'gcp',
  'ci-cd': 'devops',
  'cicd': 'devops',
  'containerization': 'docker',
  'containers': 'docker',
  'k8s': 'kubernetes',
  'postgresql': 'database',
  'mysql': 'database',
  'mongodb': 'database',
  'postgres': 'database',
  'csharp': 'c-sharp',
  'c#': 'c-sharp',
  'dotnet': 'c-sharp',
  '.net': 'c-sharp',
  'go': 'golang',
};

// Helper function to get icon for a course
export const getCourseIcon = (slug: string): JSX.Element => {
  const normalizedSlug = slug.toLowerCase().replace(/\s+/g, '-');

  // Check direct match first
  if (courseIcons[normalizedSlug]) {
    return courseIcons[normalizedSlug];
  }

  // Check aliases
  if (slugAliases[normalizedSlug] && courseIcons[slugAliases[normalizedSlug]]) {
    return courseIcons[slugAliases[normalizedSlug]];
  }

  // Check if slug contains known keywords
  const keywords = ['python', 'javascript', 'react', 'typescript', 'nodejs', 'docker', 'kubernetes',
    'aws', 'azure', 'gcp', 'cloud', 'devops', 'linux', 'database', 'sql', 'git', 'java', 'golang',
    'rust', 'html', 'css', 'security', 'cybersecurity', 'ml', 'ai', 'data-science'];

  for (const keyword of keywords) {
    if (normalizedSlug.includes(keyword) && courseIcons[keyword]) {
      return courseIcons[keyword];
    }
  }

  return courseIcons.default;
};

